import { createRouter, createWebHistory } from 'vue-router';
import LoginComponent from './components/LoginComponent.vue';
import ManageComponent from './components/ManageComponent.vue';

const routes = [
  {
    path: '/',
    redirect: '/login',
  },
  {
    path: '/login',
    name: 'login',
    component: LoginComponent,
  },
  {
    path: '/manage/:userId',
    name: 'manage',
    component: ManageComponent,
    meta: { requiresAuth: true },
  },
  // Rota curinga para URLs encurtadas
  {
    path: '/:shortURL', // :shortURL é um parâmetro dinâmico para a URL encurtada
    name: 'shortURL',
    component: ManageComponent, // Você pode redirecionar para a página apropriada aqui
    meta: { requiresAuth: true }, // Proteger a rota se necessário
  },
  // Outras rotas aqui...
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;
