import { createRouter, createWebHistory } from 'vue-router';
import LoginComponent from './components/LoginComponent.vue';
import ManageComponent from './components/ManageComponent.vue';

const routes = [
  {
    path: '/',
    redirect: '/login', // Redirecionar a rota raiz para a página de login
  },
  {
    path: '/login',
    name: 'login',
    component: LoginComponent,
  },
  {
    path: '/manage/:userId', // O parâmetro userId será parte da URL
    name: 'manage', // Nome da rota
    component: ManageComponent,
    meta: { requiresAuth: true }, // Proteger a rota com autenticação
  },
  // Outras rotas aqui...
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;
