import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import axios from 'axios';

// Configurar o Axios para usar a URL base correta
axios.defaults.baseURL = 'http://localhost:3000'; // Certifique-se de que essa URL corresponde à porta em que seu servidor Node.js está em execução

const app = createApp(App);
app.use(router); // Certifique-se de usar o Vue Router corretamente
app.mount('#app');
